        READ INPUT TAPE 1, 100, A, B, C
        READ INPUT TAPE 2, 200, X, Y, Z
        READ TAPE 3, D, E, F
        READ TAPE 4, G, H
        WRITE OUTPUT TAPE 5, 300, RESULT
        WRITE OUTPUT TAPE 6, 400, TOTAL, COUNT
        WRITE TAPE 7, P, Q, R
        WRITE TAPE 8, S
100     FORMAT (3F10.2)
200     FORMAT (3E15.6)
300     FORMAT (F20.8)
400     FORMAT (I10, F15.4)
