COMMON /BLK/ VALUES(100)
CALL INITIALIZE
CALL PROCESS
CALL OUTPUT
END

SUBROUTINE INITIALIZE
COMMON /BLK/ VALUES(100)
DO 10 I = 1, 100
    VALUES(I) = I * 2.0
10 CONTINUE
RETURN
END

SUBROUTINE PROCESS
COMMON /BLK/ VALUES(100)
DO 20 I = 1, 100
    VALUES(I) = VALUES(I) + 1.0
20 CONTINUE
RETURN
END

SUBROUTINE OUTPUT
COMMON /BLK/ VALUES(100)
DO 30 I = 1, 10
    PRINT 100, I, VALUES(I)
30 CONTINUE
100 FORMAT (I3, F8.2)
RETURN
END
