      PROGRAM CHARPERVAR
      CHARACTER NAME*30, CITY*20, STATE*2
      CHARACTER A*10, B*5
      CHARACTER*8 PREFIX, SUFFIX*4

      NAME = 'JOHN DOE'
      CITY = 'NEW YORK'
      STATE = 'NY'

      A = 'HELLO'
      B = 'WORLD'

      PREFIX = 'PREFIX'
      SUFFIX = 'SUFF'

      PRINT *, NAME
      PRINT *, CITY
      PRINT *, STATE

      END
