      PROGRAM FILEIO
      INTEGER IOS, IUNIT, NREC
      LOGICAL EXISTS, ISOPEN, HASNAME
      CHARACTER*80 FNAME, ACCVAL, FRMVAL
      OPEN(10)
      OPEN(UNIT=10, FILE='data.txt')
      OPEN(10, FILE='output.dat', STATUS='NEW')
      OPEN(20, ACCESS='DIRECT', RECL=100)
      OPEN(UNIT=30, STATUS='OLD', IOSTAT=IOS, ERR=100)
      OPEN(40, FILE='numbers.txt', BLANK='ZERO')
      CLOSE(10)
      CLOSE(UNIT=20, STATUS='KEEP')
      CLOSE(30, IOSTAT=IOS, ERR=200)
      INQUIRE(UNIT=10, EXIST=EXISTS)
      INQUIRE(FILE='data.txt', EXIST=EXISTS, OPENED=ISOPEN)
      INQUIRE(10, OPENED=ISOPEN, NAMED=HASNAME, NAME=FNAME)
      INQUIRE(20, DIRECT=ACCVAL, RECL=NREC, NEXTREC=NREC)
      INQUIRE(UNIT=30, SEQUENTIAL=ACCVAL, NUMBER=IUNIT)
      REWIND 10
      REWIND IUNIT
      BACKSPACE 10
      BACKSPACE IUNIT
      ENDFILE 10
      ENDFILE IUNIT
 100  CONTINUE
 200  CONTINUE
      END
