PROGRAM COMPLEX_PART_DESIGNATOR
  IMPLICIT NONE
  USE, INTRINSIC :: ISO_C_BINDING
  INTEGER :: I
  C_DOUBLE_COMPLEX :: Z
  C_DOUBLE_COMPLEX :: ARR(3)
  TYPE :: CONTAINER
    C_DOUBLE_COMPLEX :: MEMBER
  END TYPE CONTAINER
  TYPE(CONTAINER) :: OBJ

  I = 2
  Z = CMPLX(3.0, 4.0)
  Z%RE = 7.0
  Z%IM = -1.5
  ARR(I)%IM = Z%RE
  OBJ%MEMBER%RE = ARR(I)%IM
  OBJ%MEMBER%IM = Z%IM + ARR(1)%RE

  PRINT *, 'REAL:', Z%RE, 'IMAG:', Z%IM
  PRINT *, 'CONTAINER MEMBER REAL:', OBJ%MEMBER%RE
  PRINT *, 'CONTAINER MEMBER IMAG:', OBJ%MEMBER%IM
END PROGRAM COMPLEX_PART_DESIGNATOR
